import Foundationvar array:NSArray = ["疯狂iOS讲义", "疯狂Android讲义"	, "疯狂Ajax讲义", "疯狂XML讲义"	, "疯狂Swift讲义"]println("第一个元素：\(array.objectAtIndex(0))")println("索引为1的元素：\(array.objectAtIndex(1))")println("最后一个元素：\(array.lastObject)")// 获取从索引为2的元素开始，以及后面3个元素组成的新集合let arr1 = array.objectsAtIndexes(NSIndexSet(	indexesInRange:NSMakeRange(2, 3)))println(arr1)// 获取元素在集合中的位置let pos1 = array.indexOfObject("疯狂Android讲义")println("疯狂Android讲义的位置为：\(pos1)")// 获取元素在集合指定范围中的位置let pos2 = array.indexOfObject("疯狂Android讲义",	inRange:NSMakeRange(2, 3)) // ①println("在2~5范围疯狂Android讲义的位置为：\(pos2)")// 向数组的最后追加一个元素// 原NSArray本身并没有改变，只是将新返回的NSArray赋给arrayarray = array.arrayByAddingObject("孙悟空")// 向array数组的最后追加另一个数组的所有元素// 原NSArray本身并没有改变，只是将新返回的NSArray赋给arrayarray = array.arrayByAddingObjectsFromArray(["宝玉" , "黛玉"])for var i = 0 ; i < array.count; i++{	println(array[i])}// 获取array数组中从索引为5的元素开始，以及后面3个元素所组成的子集合let arr2:NSArray = array.subarrayWithRange(NSMakeRange(5, 3))// 将NSArray集合的元素写入文件arr2.writeToFile("myFile.txt", atomically:true)