import Foundation// 读取前面写入磁盘的文件，用文件内容来初始化NSArray集合let array = NSArray(contentsOfFile:"myFile.txt")	// 获取NSArray的顺序枚举器var en = array!.objectEnumerator()for var object:AnyObject? = en.nextObject() ;	object != nil ; object = en.nextObject(){	if object != nil	{		println(object!)	}}println("------下面逆序遍历------")// 获取NSArray的逆序枚举器	en = array!.reverseObjectEnumerator()for var object:AnyObject? = en.nextObject() ;	object != nil ; object = en.nextObject(){	if object != nil	{		println(object!)	}}