import Foundation// 读取前面写入磁盘的文件，用文件内容来初始化NSMutableArray集合// 由于该构造器是一个可能失败的构造器，因此程序需要进行强制解析let array = NSMutableArray(contentsOfFile:"myFile.txt")!array.addObject("疯狂iOS讲义")  // 向集合最后添加一个元素println("最后追加一个元素后：\(array as Array)")// 使用Array向集合尾部添加多个元素array.addObjectsFromArray(["张飞" , "关羽"])println("最后追加两个元素后：\(array as Array)")// 向集合的指定位置插入一个元素array.insertObject("疯狂Ajax讲义", atIndex:2)println("在索引为2处插入一个元素后：\(array as Array)")// 使用NSArray向集合指定位置插入多个元素array.insertObjects(["武松" , "林冲"],	atIndexes:NSIndexSet(indexesInRange:NSMakeRange(3,2)))println("插入多个元素后：\(array as Array)")array.removeLastObject()  // 删除集合最后一个元素println("删除最后一个元素后：\(array as Array)")array.removeObjectAtIndex(5)  // 删除集合中指定索引处的元素println("删除索引为5处的元素后：\(array as Array)")array.removeObjectsInRange(NSMakeRange(2, 3)) // 删除索引为2~4处的元素println("删除索引为2~4处的元素后：\(array as Array)")// 替换索引为2处的元素array.replaceObjectAtIndex(2 , withObject:"疯狂Android讲义")println("替换索引为2处的元素后：\(array as Array)")