import Foundation// 定义一个函数，该函数可把NSSet集合转换为字符串func NSCollectionToString (collection:NSSet) -> String{	let result = NSMutableString(string:"[")	// 使用快速枚举遍历NSSet集合	for obj in collection	{		result.appendString(obj as String)		result.appendString(", ")	}	// 获取字符串长度	let len = result.length	// 去掉字符串最后的两个字符	result.deleteCharactersInRange(NSMakeRange(len - 2, 2))	result.appendString("]")	return result}let set = NSCountedSet(objects: "疯狂iOS讲义", "疯狂Android讲义"	, "疯狂Ajax讲义")set.addObject("疯狂iOS讲义")set.addObject("疯狂iOS讲义")// 输出集合元素println(NSCollectionToString(set))let c1 = set.countForObject("疯狂iOS讲义")// 获取指定元素的添加次数println("\"疯狂iOS讲义\"的添加次数为：\(c1)")// 删除元素set.removeObject("疯狂iOS讲义")println("删除\"疯狂iOS讲义\"1次后的结果：\(NSCollectionToString(set))")let c2 = set.countForObject("疯狂iOS讲义")println("删除\"疯狂iOS讲义\"1次后的添加次数为：\(c2)")// 重复删除元素set.removeObject("疯狂iOS讲义")set.removeObject("疯狂iOS讲义")println("删除\"疯狂iOS讲义\"3次后的结果：\(NSCollectionToString(set))")