import Foundation// 定义一个函数，该函数可把NSSet集合转换为字符串func NSCollectionToString (collection:NSSet) -> String{	let result = NSMutableString(string:"[")	// 使用快速枚举遍历NSSet集合	for obj in collection	{		result.appendString(obj as String)		result.appendString(", ")	}	// 获取字符串长度	let len = result.length	// 去掉字符串最后的两个字符	result.deleteCharactersInRange(NSMakeRange(len - 2, 2))	result.appendString("]")	return result}// 创建一个初始容量为10的Set集合let set = NSMutableSet(capacity:10)set.addObject("疯狂iOS讲义")println("添加1个元素后：\(NSCollectionToString(set))")set.addObjectsFromArray(["疯狂Android讲义"	, "疯狂Ajax讲义" , "疯狂Swift讲义"])println("使用NSArray添加3个元素后：\(NSCollectionToString(set))")set.removeObject("疯狂Ajax讲义")println("删除1个元素后：\(NSCollectionToString(set))")// 再次创建一个Set集合let set2 = NSSet(objects:"孙悟空", "疯狂iOS讲义")// 计算两个集合的并集，直接改变set集合的元素set.unionSet(set2)// 计算两个集合的差集，直接改变set集合的元素// set.minusSet(set2)// 计算两个集合的交集，直接改变set集合的元素// set.intersectSet(set2)// 用set2的集合元素替换set的集合元素，直接改变set集合的元素// set.setSet(set2)println(NSCollectionToString(set))