import Foundation// 直接使用Swift的Dictionary创建NSDictionary对象let dict:NSDictionary = ["Objective-C": 89,	"Ruby":69,	"Swift": 91,	"Perl": 65]// 打印dict集合的所有元素println(dict as Dictionary)// 对NSDictionary的所有key进行过滤let keySet = dict.keysOfEntriesPassingTest()// 使用尾随闭包对key-value对进行过滤{(key: AnyObject!, value: AnyObject!, stop:UnsafeMutablePointer<ObjCBool>) -> Bool in	// 当value的值大于80时返回true	// 这意味着只有value的值大于80的key才会被保存下来	return (value as Int) > 80}println(keySet)