import Foundation// 直接使用Swift的Dictionary创建NSDictionary对象let dict: NSDictionary = ["one" : "Objective-C",	"two" : "Swift",	"three" : "Ruby",	"four" : "Perl"]// 打印dict集合的所有元素println(dict as Dictionary)// 获取所有直接调用value的compare:方法对所有的key进行排序。// 返回排好序的所有key组成的NSArraylet keyArr1 = dict.keysSortedByValueUsingSelector("compare:")println(keyArr1)let keyArr2 = dict.keysSortedByValueUsingComparator()// 对NSDictionary的value进行比较，字符串越长，即可认为该value越大{	// 下面定义比较大小的标准：字符串越长，即可认为value越大	if ($0 as NSString).length > ($1 as NSString).length	{		return .OrderedDescending	}	if ($0 as NSString).length < ($1 as NSString).length	{		return .OrderedAscending	}	return .OrderedSame}println(keyArr2)// 将NSDictionary的内容输出到指定文件中dict.writeToFile("mydict.txt" , atomically:true)